INSERT INTO przepisy (nazwa, skladniki, przygotowanie)
VALUES ('pierogi', '500 g mąki pszennej,pół łyżeczki soli, 4 łyżki oleju - 50 ml,szklanka gorącej wody - 250 ml', 'Do szerokiej miski przesiej mąkę. Dodaj sól oraz olej np. rzepakowy. Olej z pestek winogron oraz inne oleje o delikatnym smaku również się sprawdzą. Wlej szklankę gorącej, przegotowanej wody (250 ml) i wyrabiaj chwilę ciasto - najlepiej ręcznie. Na początku, jeśli ciasto jest gorące, możesz je zamieszać łyżką.');


